const t=JSON.parse('{"key":"v-c5b92cca","path":"/accumulation/JavaScript/%E4%BD%9C%E7%94%A8%E5%9F%9F%E5%92%8C%E4%BD%9C%E7%94%A8%E5%9F%9F%E9%93%BE.html","title":"作用域和作用域链","lang":"zh-CN","frontmatter":{"title":"作用域和作用域链","category":["面试"],"tag":["JavaScript"],"star":false,"sticky":false,"article":true,"description":"作用域 作用域：作用域是可访问变量的集合。 每一个变量、函数都有其作用的范围，超出作用不得使用，这个叫做作用域。 作用域链 访问一个变量时，会从当前作用域查找，如果当前作用域没有查找到就会向上级作用域查找，直到查到全局作用域，这个查找的过程形成的链式连接叫做作用域链。 （函数内部可以读取函数外部的变量，函数外部不能读取函数内部的变量，就是根据作用域链。）","head":[["meta",{"property":"og:url","content":"https://leoleor.github.io/Lion_Blog/accumulation/JavaScript/%E4%BD%9C%E7%94%A8%E5%9F%9F%E5%92%8C%E4%BD%9C%E7%94%A8%E5%9F%9F%E9%93%BE.html"}],["meta",{"property":"og:site_name","content":"一只攻城狮"}],["meta",{"property":"og:title","content":"作用域和作用域链"}],["meta",{"property":"og:description","content":"作用域 作用域：作用域是可访问变量的集合。 每一个变量、函数都有其作用的范围，超出作用不得使用，这个叫做作用域。 作用域链 访问一个变量时，会从当前作用域查找，如果当前作用域没有查找到就会向上级作用域查找，直到查到全局作用域，这个查找的过程形成的链式连接叫做作用域链。 （函数内部可以读取函数外部的变量，函数外部不能读取函数内部的变量，就是根据作用域链。）"}],["meta",{"property":"og:type","content":"article"}],["meta",{"property":"og:locale","content":"zh-CN"}],["meta",{"property":"og:updated_time","content":"2025-02-22T09:43:27.000Z"}],["meta",{"property":"article:author","content":"狮子"}],["meta",{"property":"article:tag","content":"JavaScript"}],["meta",{"property":"article:modified_time","content":"2025-02-22T09:43:27.000Z"}],["script",{"type":"application/ld+json"},"{\\"@context\\":\\"https://schema.org\\",\\"@type\\":\\"Article\\",\\"headline\\":\\"作用域和作用域链\\",\\"image\\":[\\"\\"],\\"dateModified\\":\\"2025-02-22T09:43:27.000Z\\",\\"author\\":[{\\"@type\\":\\"Person\\",\\"name\\":\\"狮子\\"}]}"]]},"headers":[{"level":2,"title":"作用域","slug":"作用域","link":"#作用域","children":[{"level":3,"title":"作用域链","slug":"作用域链","link":"#作用域链","children":[]}]}],"git":{"createdTime":1740217407000,"updatedTime":1740217407000,"contributors":[{"name":"狮子","email":"hzr1642921219@163.com","commits":1}]},"readingTime":{"minutes":0.59,"words":177},"filePathRelative":"accumulation/JavaScript/作用域和作用域链.md","localizedDate":"2025年2月22日","excerpt":"<h2> 作用域</h2>\\n<p>作用域：作用域是可访问变量的集合。<br>\\n每一个变量、函数都有其作用的范围，超出作用不得使用，这个叫做作用域。</p>\\n<h3> 作用域链</h3>\\n<p>访问一个变量时，会从当前作用域查找，如果当前作用域没有查找到就会向上级作用域查找，直到查到全局作用域，这个查找的过程形成的链式连接叫做作用域链。\\n（函数内部可以读取函数外部的变量，函数外部不能读取函数内部的变量，就是根据作用域链。）</p>\\n","autoDesc":true}');export{t as data};
